import styled from "styled-components";

export const StyledDisplay = styled.div`
  box-sizing: border-box;
  display: flex;
  align-items: center;
  margin: 10px 15px;
  padding: 20px;

  width: 100%;
  border-radius: 20px;
  background: #fff;
  font-family: Pixel, Arial, Helvetica, sans-serif;
  font-size: 0.8rem;
  color: ${(props) => props.color};
`;
