    drop();
  };
  const move = ({ keyCode }) => {
    console.log(keyCode);
    if (!gameOver) {
      if (keyCode === 37) {
        movePlayer(-1);
      } else if (keyCode === 39) {
        movePlayer(1);
      } else if (keyCode === 40) {
        dropPlayer();
      }
    } // TODO: add space bar
  };

  return (
    <div
      className={classes.TetrisWrapper}
      role="button"
      tabIndex="0"
      onKeyDown={(e) => move(e)}
    >
      <div className={classes.Tetris}>
        <Stage stage={stage} /> {/*initially is createStage() */}
        <aside className={classes.Sidebar}>
          {gameOver ? (
            <Display gameOver={gameOver} text="Game Over" />
          ) : (
            <div>
              <Display text="Score" />
              <Display text="Rows" />
              <Display text="Level" />
            </div>
          )}
          <StartButton />
        </aside>
      </div>
    </div>
  );
};

export default Tetris;
