import styled from "styled-components";

export const StyledStageWrapper = styled.div`
  display: grid;
  grid-gap: 1px;
  border: 2px solid rgb(224, 224, 224);
  width: 100%;
  max-width: 30vw;
  background: rgb(0, 0, 0, 0);
  box-sizing: border-box;
  grid-template-rows: repeat(
    ${(props) => props.stage.length} calc(30vw / ${props.stage[0].length})
  );
  grid-template-columns: repeat(${(props) => props.stage[0].length}, auto);
`;
//refer to gameHelper on the grid height and width
